    box-shadow: var(--shadow-glow);
  }
  .transition-card {
    transition: all 0.2s ease-out;
  }
  .gradient-primary {
    background: linear-gradient(135deg, hsl(245 58% 51%), hsl(245 58% 65%));
  }
  .gradient-secondary {
    background: linear-gradient(135deg, hsl(38 92% 50%), hsl(45 92% 55%));
  }
  .text-gradient-primary {
    background: linear-gradient(135deg, hsl(245 58% 51%), hsl(245 58% 65%));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
}

