import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from '@/components/ui/dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from '@/components/ui/alert-dialog';
import { Plus, Pencil, Trash2, Check, X } from 'lucide-react';
import { format } from 'date-fns';
import { de } from 'date-fns/locale';

// Types
export interface Registration {
  record_id: string;
  participant_id: string;
  participant_name?: string;
  course_id: string;
  course_title?: string;
  registration_date: string;
  paid: boolean;
}

interface Course {
  record_id: string;
  title: string;
}

interface Participant {
  record_id: string;
  name: string;
}

// Mock data
const mockCourses: Course[] = [
  { record_id: '1', title: 'React Grundlagen' },
  { record_id: '2', title: 'TypeScript f√ºr Fortgeschrittene' },
];

const mockParticipants: Participant[] = [
  { record_id: '1', name: 'Lisa Meyer' },
  { record_id: '2', name: 'Peter Hoffmann' },
  { record_id: '3', name: 'Julia Becker' },
];

const mockRegistrations: Registration[] = [
  {
    record_id: '1',
    participant_id: '1',
    participant_name: 'Lisa Meyer',
    course_id: '1',
    course_title: 'React Grundlagen',
    registration_date: '2024-02-15',
    paid: true
  },
  {
    record_id: '2',
    participant_id: '2',
    participant_name: 'Peter Hoffmann',
    course_id: '1',
    course_title: 'React Grundlagen',
    registration_date: '2024-02-16',
    paid: false
  },
  {
    record_id: '3',
    participant_id: '3',
    participant_name: 'Julia Becker',
    course_id: '2',
    course_title: 'TypeScript f√ºr Fortgeschrittene',
    registration_date: '2024-02-18',
    paid: true
  },
];

export function RegistrationsTab() {
  const [registrations, setRegistrations] = useState<Registration[]>(mockRegistrations);
  const [courses] = useState<Course[]>(mockCourses);
  const [participants] = useState<Participant[]>(mockParticipants);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingRegistration, setEditingRegistration] = useState<Registration | null>(null);
  const [formData, setFormData] = useState({
    participant_id: '',
    course_id: '',
    registration_date: new Date().toISOString().split('T')[0],
    paid: false
  });

  const resetForm = () => {
    setFormData({
      participant_id: '',
      course_id: '',
      registration_date: new Date().toISOString().split('T')[0],
      paid: false
    });
    setEditingRegistration(null);
  };

  const openCreateDialog = () => {
    resetForm();
    setIsDialogOpen(true);
  };

  const openEditDialog = (registration: Registration) => {
    setEditingRegistration(registration);
    setFormData({
      participant_id: registration.participant_id,
      course_id: registration.course_id,
      registration_date: registration.registration_date,
      paid: registration.paid
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = () => {
    if (editingRegistration) {
      setRegistrations(registrations.map(r =>
        r.record_id === editingRegistration.record_id
          ? {
              ...r,
              ...formData,
              participant_name: participants.find(p => p.record_id === formData.participant_id)?.name,
              course_title: courses.find(c => c.record_id === formData.course_id)?.title
            }
          : r
      ));
    } else {
      const newRegistration: Registration = {
        record_id: Date.now().toString(),
        ...formData,
        participant_name: participants.find(p => p.record_id === formData.participant_id)?.name,
        course_title: courses.find(c => c.record_id === formData.course_id)?.title
      };
      setRegistrations([...registrations, newRegistration]);
    }
    setIsDialogOpen(false);
    resetForm();
  };

  const handleDelete = (id: string) => {
    setRegistrations(registrations.filter(r => r.record_id !== id));
  };

  const togglePaid = (registration: Registration) => {
    setRegistrations(registrations.map(r =>
      r.record_id === registration.record_id
        ? { ...r, paid: !r.paid }
        : r
    ));
  };

  const formatDate = (dateStr: string) => {
    try {
      return format(new Date(dateStr), 'dd.MM.yyyy', { locale: de });
    } catch {
      return dateStr;
    }
  };

  return (
    <Card className="shadow-card">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle className="text-xl font-semibold">Anmeldungen</CardTitle>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={openCreateDialog} className="gap-2">
              <Plus className="h-4 w-4" />
              Neue Anmeldung
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>{editingRegistration ? 'Anmeldung bearbeiten' : 'Neue Anmeldung'}</DialogTitle>
              <DialogDescription>
                {editingRegistration ? 'Bearbeiten Sie die Anmeldung.' : 'Melden Sie einen Teilnehmer f√ºr einen Kurs an.'}
              </DialogDescription>
            </DialogHeader>
            <div className="grid gap-4 py-4">
              <div className="grid gap-2">
                <Label htmlFor="participant">Teilnehmer</Label>
                <Select
                  value={formData.participant_id}
                  onValueChange={(value) => setFormData({ ...formData, participant_id: value })}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Teilnehmer ausw√§hlen" />
                  </SelectTrigger>
                  <SelectContent>
                    {participants.map((participant) => (
                      <SelectItem key={participant.record_id} value={participant.record_id}>
                        {participant.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="grid gap-2">
                <Label htmlFor="course">Kurs</Label>
                <Select
                  value={formData.course_id}
                  onValueChange={(value) => setFormData({ ...formData, course_id: value })}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Kurs ausw√§hlen" />
                  </SelectTrigger>
                  <SelectContent>
                    {courses.map((course) => (
                      <SelectItem key={course.record_id} value={course.record_id}>
                        {course.title}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="grid gap-2">
                <Label htmlFor="registration_date">Anmeldedatum</Label>
                <Input
                  id="registration_date"
                  type="date"
                  value={formData.registration_date}
                  onChange={(e) => setFormData({ ...formData, registration_date: e.target.value })}
                />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="paid">Bezahlt</Label>
                <Select
                  value={formData.paid ? 'yes' : 'no'}
                  onValueChange={(value) => setFormData({ ...formData, paid: value === 'yes' })}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="yes">Ja</SelectItem>
                    <SelectItem value="no">Nein</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
            <DialogFooter>
              <Button variant="outline" onClick={() => setIsDialogOpen(false)}>
                Abbrechen
              </Button>
              <Button onClick={handleSubmit}>
                {editingRegistration ? 'Speichern' : 'Erstellen'}
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </CardHeader>
      <CardContent>
        <div className="rounded-lg border overflow-hidden">
          <Table>
            <TableHeader>
              <TableRow className="bg-muted/50">
                <TableHead>Teilnehmer</TableHead>
                <TableHead>Kurs</TableHead>
                <TableHead>Anmeldedatum</TableHead>
                <TableHead className="text-center">Bezahlt</TableHead>
                <TableHead className="w-[100px]"></TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {registrations.map((registration) => (
                <TableRow key={registration.record_id} className="transition-card hover:bg-accent/50">
                  <TableCell className="font-medium">{registration.participant_name}</TableCell>
                  <TableCell>
                    <Badge variant="outline">{registration.course_title}</Badge>
                  </TableCell>
                  <TableCell>{formatDate(registration.registration_date)}</TableCell>
                  <TableCell className="text-center">
                    <Button
                      variant="ghost"
                      size="icon-sm"
                      onClick={() => togglePaid(registration)}
                    >
                      {registration.paid ? (
                        <Badge variant="success" className="gap-1">
                          <Check className="h-3 w-3" />
                          Bezahlt
                        </Badge>
                      ) : (
                        <Badge variant="warning" className="gap-1">
                          <X className="h-3 w-3" />
                          Offen
                        </Badge>
                      )}
                    </Button>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-1 justify-end">
                      <Button
                        variant="ghost"
                        size="icon-sm"
                        onClick={() => openEditDialog(registration)}
                      >
                        <Pencil className="h-4 w-4" />
                      </Button>
                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button variant="ghost" size="icon-sm">
                            <Trash2 className="h-4 w-4 text-destructive" />
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle>Anmeldung l√∂schen?</AlertDialogTitle>
                            <AlertDialogDescription>
                              M√∂chten Sie diese Anmeldung wirklich l√∂schen?
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Abbrechen</AlertDialogCancel>
                            <AlertDialogAction
                              onClick={() => handleDelete(registration.record_id)}
                              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                            >
                              L√∂schen
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
              {registrations.length === 0 && (
                <TableRow>
                  <TableCell colSpan={5} className="text-center py-8 text-muted-foreground">
                    Keine Anmeldungen vorhanden. Erstellen Sie Ihre erste Anmeldung.
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </div>
      </CardContent>
    </Card>
  );
}

