import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Badge } from '@/components/ui/badge';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from '@/components/ui/dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from '@/components/ui/alert-dialog';
import { Plus, Pencil, Trash2, Users } from 'lucide-react';
import { format } from 'date-fns';
import { de } from 'date-fns/locale';

// Types
export interface Course {
  record_id: string;
  title: string;
  description: string;
  start_date: string;
  end_date: string;
  max_participants: number;
  price: number;
  instructor_id?: string;
  room_id?: string;
  instructor_name?: string;
  room_name?: string;
}

export interface Instructor {
  record_id: string;
  name: string;
}

export interface Room {
  record_id: string;
  name: string;
}

// Mock data
const mockCourses: Course[] = [
  {
    record_id: '1',
    title: 'React Grundlagen',
    description: 'Einf√ºhrung in React und moderne Webentwicklung',
    start_date: '2024-03-01',
    end_date: '2024-03-15',
    max_participants: 20,
    price: 499,
    instructor_name: 'Dr. Max M√ºller',
    room_name: 'Raum A1'
  },
  {
    record_id: '2',
    title: 'TypeScript f√ºr Fortgeschrittene',
    description: 'Vertiefung in TypeScript mit praktischen √úbungen',
    start_date: '2024-04-01',
    end_date: '2024-04-10',
    max_participants: 15,
    price: 599,
    instructor_name: 'Anna Schmidt',
    room_name: 'Raum B2'
  },
];

const mockInstructors: Instructor[] = [
  { record_id: '1', name: 'Dr. Max M√ºller' },
  { record_id: '2', name: 'Anna Schmidt' },
];

const mockRooms: Room[] = [
  { record_id: '1', name: 'Raum A1' },
  { record_id: '2', name: 'Raum B2' },
];

export function CoursesTab() {
  const [courses, setCourses] = useState<Course[]>(mockCourses);
  const [instructors] = useState<Instructor[]>(mockInstructors);
  const [rooms] = useState<Room[]>(mockRooms);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingCourse, setEditingCourse] = useState<Course | null>(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    start_date: '',
    end_date: '',
    max_participants: 20,
    price: 0,
    instructor_id: '',
    room_id: ''
  });

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      start_date: '',
      end_date: '',
      max_participants: 20,
      price: 0,
      instructor_id: '',
      room_id: ''
    });
    setEditingCourse(null);
  };

  const openCreateDialog = () => {
    resetForm();
    setIsDialogOpen(true);
  };

  const openEditDialog = (course: Course) => {
    setEditingCourse(course);
    setFormData({
      title: course.title,
      description: course.description,
      start_date: course.start_date,
      end_date: course.end_date,
      max_participants: course.max_participants,
      price: course.price,
      instructor_id: course.instructor_id || '',
      room_id: course.room_id || ''
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = () => {
    if (editingCourse) {
      setCourses(courses.map(c =>
        c.record_id === editingCourse.record_id
          ? {
              ...c,
              ...formData,
              instructor_name: instructors.find(i => i.record_id === formData.instructor_id)?.name,
              room_name: rooms.find(r => r.record_id === formData.room_id)?.name
            }
          : c
      ));
    } else {
      const newCourse: Course = {
        record_id: Date.now().toString(),
        ...formData,
        instructor_name: instructors.find(i => i.record_id === formData.instructor_id)?.name,
        room_name: rooms.find(r => r.record_id === formData.room_id)?.name
      };
      setCourses([...courses, newCourse]);
    }
    setIsDialogOpen(false);
    resetForm();
  };

  const handleDelete = (id: string) => {
    setCourses(courses.filter(c => c.record_id !== id));
  };

  const formatDate = (dateStr: string) => {
    try {
      return format(new Date(dateStr), 'dd.MM.yyyy', { locale: de });
    } catch {
      return dateStr;
    }
  };

  return (
    <Card className="shadow-card">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle className="text-xl font-semibold">Kurse</CardTitle>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={openCreateDialog} className="gap-2">
              <Plus className="h-4 w-4" />
              Neuer Kurs
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-lg">
            <DialogHeader>
              <DialogTitle>{editingCourse ? 'Kurs bearbeiten' : 'Neuer Kurs'}</DialogTitle>
              <DialogDescription>
                {editingCourse ? 'Bearbeiten Sie die Kursdaten.' : 'Erstellen Sie einen neuen Kurs.'}
              </DialogDescription>
            </DialogHeader>
            <div className="grid gap-4 py-4">
              <div className="grid gap-2">
                <Label htmlFor="title">Titel</Label>
                <Input
                  id="title"
                  value={formData.title}
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                  placeholder="Kurstitel"
                />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="description">Beschreibung</Label>
                <Textarea
                  id="description"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  placeholder="Kursbeschreibung"
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="grid gap-2">
                  <Label htmlFor="start_date">Startdatum</Label>
                  <Input
                    id="start_date"
                    type="date"
                    value={formData.start_date}
                    onChange={(e) => setFormData({ ...formData, start_date: e.target.value })}
                  />
                </div>
                <div className="grid gap-2">
                  <Label htmlFor="end_date">Enddatum</Label>
                  <Input
                    id="end_date"
                    type="date"
                    value={formData.end_date}
                    onChange={(e) => setFormData({ ...formData, end_date: e.target.value })}
                  />
                </div>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="grid gap-2">
                  <Label htmlFor="max_participants">Max. Teilnehmer</Label>
                  <Input
                    id="max_participants"
                    type="number"
                    value={formData.max_participants}
                    onChange={(e) => setFormData({ ...formData, max_participants: parseInt(e.target.value) || 0 })}
                  />
                </div>
                <div className="grid gap-2">
                  <Label htmlFor="price">Preis (EUR)</Label>
                  <Input
                    id="price"
                    type="number"
                    step="0.01"
                    value={formData.price}
                    onChange={(e) => setFormData({ ...formData, price: parseFloat(e.target.value) || 0 })}
                  />
                </div>
              </div>
              <div className="grid gap-2">
                <Label htmlFor="instructor">Dozent</Label>
                <Select
                  value={formData.instructor_id}
                  onValueChange={(value) => setFormData({ ...formData, instructor_id: value })}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Dozent ausw√§hlen" />
                  </SelectTrigger>
                  <SelectContent>
                    {instructors.map((instructor) => (
                      <SelectItem key={instructor.record_id} value={instructor.record_id}>
                        {instructor.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="grid gap-2">
                <Label htmlFor="room">Raum</Label>
                <Select
                  value={formData.room_id}
                  onValueChange={(value) => setFormData({ ...formData, room_id: value })}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Raum ausw√§hlen" />
                  </SelectTrigger>
                  <SelectContent>
                    {rooms.map((room) => (
                      <SelectItem key={room.record_id} value={room.record_id}>
                        {room.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
            <DialogFooter>
              <Button variant="outline" onClick={() => setIsDialogOpen(false)}>
                Abbrechen
              </Button>
              <Button onClick={handleSubmit}>
                {editingCourse ? 'Speichern' : 'Erstellen'}
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </CardHeader>
      <CardContent>
        <div className="rounded-lg border overflow-hidden">
          <Table>
            <TableHeader>
              <TableRow className="bg-muted/50">
                <TableHead>Titel</TableHead>
                <TableHead>Zeitraum</TableHead>
                <TableHead>Dozent</TableHead>
                <TableHead>Raum</TableHead>
                <TableHead className="text-center">
                  <Users className="h-4 w-4 mx-auto" />
                </TableHead>
                <TableHead className="text-right">Preis</TableHead>
                <TableHead className="w-[100px]"></TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {courses.map((course) => (
                <TableRow key={course.record_id} className="transition-card hover:bg-accent/50">
                  <TableCell>
                    <div>
                      <div className="font-medium">{course.title}</div>
                      <div className="text-sm text-muted-foreground line-clamp-1">
                        {course.description}
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="text-sm">
                      {formatDate(course.start_date)} - {formatDate(course.end_date)}
                    </div>
                  </TableCell>
                  <TableCell>
                    {course.instructor_name && (
                      <Badge variant="outline">{course.instructor_name}</Badge>
                    )}
                  </TableCell>
                  <TableCell>
                    {course.room_name && (
                      <Badge variant="secondary">{course.room_name}</Badge>
                    )}
                  </TableCell>
                  <TableCell className="text-center">
                    <Badge variant="outline">{course.max_participants}</Badge>
                  </TableCell>
                  <TableCell className="text-right font-medium">
                    {course.price.toLocaleString('de-DE')} EUR
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-1 justify-end">
                      <Button
                        variant="ghost"
                        size="icon-sm"
                        onClick={() => openEditDialog(course)}
                      >
                        <Pencil className="h-4 w-4" />
                      </Button>
                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button variant="ghost" size="icon-sm">
                            <Trash2 className="h-4 w-4 text-destructive" />
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle>Kurs l√∂schen?</AlertDialogTitle>
                            <AlertDialogDescription>
                              M√∂chten Sie den Kurs "{course.title}" wirklich l√∂schen? Diese Aktion kann nicht r√ºckg√§ngig gemacht werden.
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Abbrechen</AlertDialogCancel>
                            <AlertDialogAction
                              onClick={() => handleDelete(course.record_id)}
                              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                            >
                              L√∂schen
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
              {courses.length === 0 && (
                <TableRow>
                  <TableCell colSpan={7} className="text-center py-8 text-muted-foreground">
                    Keine Kurse vorhanden. Erstellen Sie Ihren ersten Kurs.
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </div>
      </CardContent>
    </Card>
  );
}

