import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from '@/components/ui/dialog';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from '@/components/ui/alert-dialog';
import { Plus, Pencil, Trash2, Mail, Phone, BookOpen } from 'lucide-react';

// Types
export interface Instructor {
  record_id: string;
  name: string;
  email: string;
  phone: string;
  specialty: string;
}

// Mock data
const mockInstructors: Instructor[] = [
  {
    record_id: '1',
    name: 'Dr. Max M√ºller',
    email: 'max.mueller@example.com',
    phone: '+49 151 12345678',
    specialty: 'Webentwicklung'
  },
  {
    record_id: '2',
    name: 'Anna Schmidt',
    email: 'anna.schmidt@example.com',
    phone: '+49 152 87654321',
    specialty: 'TypeScript & Node.js'
  },
  {
    record_id: '3',
    name: 'Thomas Weber',
    email: 'thomas.weber@example.com',
    phone: '+49 153 11223344',
    specialty: 'Datenbanken & SQL'
  },
];

export function InstructorsTab() {
  const [instructors, setInstructors] = useState<Instructor[]>(mockInstructors);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingInstructor, setEditingInstructor] = useState<Instructor | null>(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    specialty: ''
  });

  const resetForm = () => {
    setFormData({ name: '', email: '', phone: '', specialty: '' });
    setEditingInstructor(null);
  };

  const openCreateDialog = () => {
    resetForm();
    setIsDialogOpen(true);
  };

  const openEditDialog = (instructor: Instructor) => {
    setEditingInstructor(instructor);
    setFormData({
      name: instructor.name,
      email: instructor.email,
      phone: instructor.phone,
      specialty: instructor.specialty
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = () => {
    if (editingInstructor) {
      setInstructors(instructors.map(i =>
        i.record_id === editingInstructor.record_id
          ? { ...i, ...formData }
          : i
      ));
    } else {
      const newInstructor: Instructor = {
        record_id: Date.now().toString(),
        ...formData
      };
      setInstructors([...instructors, newInstructor]);
    }
    setIsDialogOpen(false);
    resetForm();
  };

  const handleDelete = (id: string) => {
    setInstructors(instructors.filter(i => i.record_id !== id));
  };

  return (
    <Card className="shadow-card">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle className="text-xl font-semibold">Dozenten</CardTitle>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={openCreateDialog} className="gap-2">
              <Plus className="h-4 w-4" />
              Neuer Dozent
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>{editingInstructor ? 'Dozent bearbeiten' : 'Neuer Dozent'}</DialogTitle>
              <DialogDescription>
                {editingInstructor ? 'Bearbeiten Sie die Dozentendaten.' : 'F√ºgen Sie einen neuen Dozenten hinzu.'}
              </DialogDescription>
            </DialogHeader>
            <div className="grid gap-4 py-4">
              <div className="grid gap-2">
                <Label htmlFor="name">Name</Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  placeholder="Vollst√§ndiger Name"
                />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="email">E-Mail</Label>
                <Input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  placeholder="email@example.com"
                />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="phone">Telefon</Label>
                <Input
                  id="phone"
                  value={formData.phone}
                  onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                  placeholder="+49 151 12345678"
                />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="specialty">Fachgebiet</Label>
                <Input
                  id="specialty"
                  value={formData.specialty}
                  onChange={(e) => setFormData({ ...formData, specialty: e.target.value })}
                  placeholder="z.B. Webentwicklung"
                />
              </div>
            </div>
            <DialogFooter>
              <Button variant="outline" onClick={() => setIsDialogOpen(false)}>
                Abbrechen
              </Button>
              <Button onClick={handleSubmit}>
                {editingInstructor ? 'Speichern' : 'Erstellen'}
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </CardHeader>
      <CardContent>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {instructors.map((instructor) => (
            <Card key={instructor.record_id} className="shadow-card transition-card hover:shadow-card-hover">
              <CardContent className="pt-6">
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-center gap-3">
                    <div className="w-12 h-12 rounded-full gradient-primary flex items-center justify-center text-primary-foreground font-semibold text-lg">
                      {instructor.name.split(' ').map(n => n[0]).join('')}
                    </div>
                    <div>
                      <h3 className="font-semibold text-foreground">{instructor.name}</h3>
                      <Badge variant="outline" className="mt-1">
                        <BookOpen className="h-3 w-3 mr-1" />
                        {instructor.specialty}
                      </Badge>
                    </div>
                  </div>
                  <div className="flex gap-1">
                    <Button
                      variant="ghost"
                      size="icon-sm"
                      onClick={() => openEditDialog(instructor)}
                    >
                      <Pencil className="h-4 w-4" />
                    </Button>
                    <AlertDialog>
                      <AlertDialogTrigger asChild>
                        <Button variant="ghost" size="icon-sm">
                          <Trash2 className="h-4 w-4 text-destructive" />
                        </Button>
                      </AlertDialogTrigger>
                      <AlertDialogContent>
                        <AlertDialogHeader>
                          <AlertDialogTitle>Dozent l√∂schen?</AlertDialogTitle>
                          <AlertDialogDescription>
                            M√∂chten Sie den Dozenten "{instructor.name}" wirklich l√∂schen?
                          </AlertDialogDescription>
                        </AlertDialogHeader>
                        <AlertDialogFooter>
                          <AlertDialogCancel>Abbrechen</AlertDialogCancel>
                          <AlertDialogAction
                            onClick={() => handleDelete(instructor.record_id)}
                            className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                          >
                            L√∂schen
                          </AlertDialogAction>
                        </AlertDialogFooter>
                      </AlertDialogContent>
                    </AlertDialog>
                  </div>
                </div>
                <div className="space-y-2 text-sm">
                  <div className="flex items-center gap-2 text-muted-foreground">
                    <Mail className="h-4 w-4" />
                    <span>{instructor.email}</span>
                  </div>
                  <div className="flex items-center gap-2 text-muted-foreground">
                    <Phone className="h-4 w-4" />
                    <span>{instructor.phone}</span>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
          {instructors.length === 0 && (
            <div className="col-span-full text-center py-8 text-muted-foreground">
              Keine Dozenten vorhanden. F√ºgen Sie Ihren ersten Dozenten hinzu.
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

