import { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from '@/components/ui/dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from '@/components/ui/alert-dialog';
import { Plus, Pencil, Trash2 } from 'lucide-react';
import { format } from 'date-fns';
import { de } from 'date-fns/locale';

// Types
export interface Participant {
  record_id: string;
  name: string;
  email: string;
  phone: string;
  birth_date: string;
}

// Mock data
const mockParticipants: Participant[] = [
  {
    record_id: '1',
    name: 'Lisa Meyer',
    email: 'lisa.meyer@example.com',
    phone: '+49 160 11111111',
    birth_date: '1995-06-15'
  },
  {
    record_id: '2',
    name: 'Peter Hoffmann',
    email: 'peter.hoffmann@example.com',
    phone: '+49 161 22222222',
    birth_date: '1988-03-22'
  },
  {
    record_id: '3',
    name: 'Julia Becker',
    email: 'julia.becker@example.com',
    phone: '+49 162 33333333',
    birth_date: '1992-11-08'
  },
];

export function ParticipantsTab() {
  const [participants, setParticipants] = useState<Participant[]>(mockParticipants);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingParticipant, setEditingParticipant] = useState<Participant | null>(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    birth_date: ''
  });

  const resetForm = () => {
    setFormData({ name: '', email: '', phone: '', birth_date: '' });
    setEditingParticipant(null);
  };

  const openCreateDialog = () => {
    resetForm();
    setIsDialogOpen(true);
  };

  const openEditDialog = (participant: Participant) => {
    setEditingParticipant(participant);
    setFormData({
      name: participant.name,
      email: participant.email,
      phone: participant.phone,
      birth_date: participant.birth_date
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = () => {
    if (editingParticipant) {
      setParticipants(participants.map(p =>
        p.record_id === editingParticipant.record_id
          ? { ...p, ...formData }
          : p
      ));
    } else {
      const newParticipant: Participant = {
        record_id: Date.now().toString(),
        ...formData
      };
      setParticipants([...participants, newParticipant]);
    }
    setIsDialogOpen(false);
    resetForm();
  };

  const handleDelete = (id: string) => {
    setParticipants(participants.filter(p => p.record_id !== id));
  };

  const formatDate = (dateStr: string) => {
    try {
      return format(new Date(dateStr), 'dd.MM.yyyy', { locale: de });
    } catch {
      return dateStr;
    }
  };

  return (
    <Card className="shadow-card">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle className="text-xl font-semibold">Teilnehmer</CardTitle>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={openCreateDialog} className="gap-2">
              <Plus className="h-4 w-4" />
              Neuer Teilnehmer
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>{editingParticipant ? 'Teilnehmer bearbeiten' : 'Neuer Teilnehmer'}</DialogTitle>
              <DialogDescription>
                {editingParticipant ? 'Bearbeiten Sie die Teilnehmerdaten.' : 'F√ºgen Sie einen neuen Teilnehmer hinzu.'}
              </DialogDescription>
            </DialogHeader>
            <div className="grid gap-4 py-4">
              <div className="grid gap-2">
                <Label htmlFor="name">Name</Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  placeholder="Vollst√§ndiger Name"
                />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="email">E-Mail</Label>
                <Input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  placeholder="email@example.com"
                />
              </div>
              <div className="grid gap-2">
