import { Plus, Pencil, Trash2, Building2, Users } from 'lucide-react';

// Types
export interface Room {
  record_id: string;
  name: string;
  building: string;
  capacity: number;
}

// Mock data
const mockRooms: Room[] = [
  {
    record_id: '1',
    name: 'Raum A1',
    building: 'Hauptgeb√§ude',
    capacity: 30
  },
  {
    record_id: '2',
