    capacity: 20
  },
  {
    record_id: '3',
    name: 'Seminarraum 1',
    building: 'Hauptgeb√§ude',
    capacity: 15
  },
  {
    record_id: '4',
    name: 'PC-Labor',
    building: 'Technikzentrum',
    capacity: 25
  },
];

export function RoomsTab() {
  const [rooms, setRooms] = useState<Room[]>(mockRooms);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingRoom, setEditingRoom] = useState<Room | null>(null);
  const [formData, setFormData] = useState({
    name: '',
    building: '',
    capacity: 20
  });

  const resetForm = () => {
    setFormData({ name: '', building: '', capacity: 20 });
    setEditingRoom(null);
  };

  const openCreateDialog = () => {
    resetForm();
    setIsDialogOpen(true);
  };

  const openEditDialog = (room: Room) => {
    setEditingRoom(room);
    setFormData({
      name: room.name,
      building: room.building,
      capacity: room.capacity
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = () => {
    if (editingRoom) {
      setRooms(rooms.map(r =>
        r.record_id === editingRoom.record_id
          ? { ...r, ...formData }
          : r
      ));
    } else {
      const newRoom: Room = {
        record_id: Date.now().toString(),
        ...formData
      };
      setRooms([...rooms, newRoom]);
    }
    setIsDialogOpen(false);
    resetForm();
  };

  const handleDelete = (id: string) => {
    setRooms(rooms.filter(r => r.record_id !== id));
  };

  return (
    <Card className="shadow-card">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle className="text-xl font-semibold">R√§ume</CardTitle>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={openCreateDialog} className="gap-2">
              <Plus className="h-4 w-4" />
              Neuer Raum
